import React, { Component } from 'react';
import './App.css';
import * as configcat from 'configcat-js';
import Demo from './Demo';

class App extends Component {

  constructor(props) {
    super(props)
    this.client = configcat.createClientWithAutoPoll("PKDVCLf-Hq-h-kCzMp-L7Q/HhOWfwVtZ0mb30i9wi17GQ", { 
      pollIntervalSeconds: 2, 
      logger: configcat.createConsoleLogger(3) // Setting log level to 3 (Info)
    });
      // You can instantiate the client with different polling modes. See the Docs: https://configcat.com/docs/sdk-reference/js/#polling-modes
  }
  
  render() {
    return (
      <div className="wrapper">
        <div className="heading"><h1>Welcome to ConfigCat Sample app for React.js!</h1></div>
        <div className="logos">
          <img className="cat" width="100" alt="ConfigCat Logo" src="data:image/svg+xml;base64," />
          <span role="img" aria-label="heart" className="heart">❤️</span>
          <img width="140" alt="React Logo" src="data:image/svg+xml;base64," />
        </div>
        <Demo client={this.client}></Demo>
      </div>
    );
  }
}

export default App;
